const React = require('react');
import { render } from '@testing-library/react';
import { useState } from 'react';
import Navigation from './navbar';
import Form from 'react-bootstrap/Form'
import InputGroup from 'react-bootstrap/InputGroup'
import FormControl from 'react-bootstrap/FormControl';

function CreateGuide(){

    

    function addStep() {
       
        var newLabel = document.createElement('label')
        newLabel.innerHTML = 'Step'

        var newInput = document.createElement('input');
        newInput.setAttribute('type', 'text')
    
        var newImageLabel = document.createElement('label')
        newImageLabel.innerHTML = 'Image'

        var newImage = document.createElement('input');
        newImage.setAttribute('type', 'text')

        document.getElementById("newStep").appendChild(newLabel);
        document.getElementById("newStep").appendChild(newInput);
        document.getElementById("newStep").appendChild(newImageLabel);
        document.getElementById("newStep").appendChild(newImage);
        
    }



  
    return (
        <main>
            <div>
                <Navigation />
            </div>
            <h1>Create a Tutorial!</h1>
            <Form>
                <Form.Group className="mb-3" controlId="formBasicTitle">
                    <Form.Label>Title</Form.Label>
                    <Form.Control type="text" placeholder="How to..." required />
                </Form.Group>

                <InputGroup className="mb-3" controlId="formBasicDescription">
                    <InputGroup.Text>Description</InputGroup.Text>
                    <FormControl as="textarea" aria-label="Description" placeholder="Tell DIY-er's what you will be teaching them..." required />
                </InputGroup>

                <InputGroup className="mb-3" controlId="formBasicStep">
                    <InputGroup.Text>Step One</InputGroup.Text>
                    <FormControl as="textarea" aria-label="Step One" placeholder="The first step is to..." required />
                </InputGroup>

                <Form.Group controlId="formFile" className="mb-3">
                    <Form.Label>Step One Image</Form.Label>
                    <Form.Control type="file" />
                </Form.Group>

                <Button variant="light" type="submit" onClick={addStep} >
                    Step One Image
                </Button>
                
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </main>
    )
    
}

export default CreateGuide
